                padding-bottom: 10px;
            }
            div.cma-avoid-page-break {
                page-break-inside: avoid;
            }
            div#cma-summary {
                page-break-after: always;
            }
            div#cma-faq {
                page-break-after: always;
            }
            table.cma-content {
                border-collapse: collapse;
                margin-left: 20px;
            }
            table.cma-hashes {
                table-layout: fixed;
                width: 880px;
            }
            table.cma-hashes td{
                  width: 780px;
                  word-wrap: break-word;
            }
            .cma-left th {
                text-align: right;
                vertical-align: top;
                padding: 3px 8px 3px 20px;
                background-color: #f0f0f0;
                border-right: 1px solid #aaa;
            }
            .cma-left td {
                padding-left: 8px;
            }
            .cma-color-title th, .cma-color-list th, .cma-color-title-only th {
                text-align: left;
                padding: 3px 0 3px 20px;
                background-color: #f0f0f0;
            }
            .cma-color-title td, .cma-color-list td, .cma-color-title-only td {
                padding: 3px 20px;
            }
            .cma-color-title tr:nth-child(odd) {
                background-color: #f0f0f0;
            }
            .cma-color-list tr:nth-child(even) {
                background-color: #f0f0f0;
            }
            td.cma-relationship {
                max-width: 310px;
                word-wrap: break-word;
            }
            ul.cma-ul {
                margin: 5px 0 10px 0;
            }
            ul.cma-ul li {
                line-height: 20px;
                margin-bottom: 5px;
                word-wrap: break-word;
            }
            #cma-survey {
                font-weight: bold;
                font-style: italic;
            }
            div.cma-banner-container {
                position: relative;
                text-align: center;
                color: white;
            }
            img.cma-banner {
                max-width: 900px;
                height: auto;
            }
            img.cma-nccic-logo {
                max-height: 60px;
                width: auto;
                float: left;
                margin-top: -15px;
            }
            div.cma-report-name {
                position: absolute;
                bottom: 32px;
                left: 12px;
                font-size: 20px;
            }
            div.cma-report-number {
                position: absolute;
                bottom: 70px;
                right: 100px;
                font-size: 18px;
            }
            div.cma-report-date {
                position: absolute;
                bottom: 32px;
                right: 100px;
                font-size: 18px;
            }
            img.cma-thumbnail {
                max-height: 100px;
                width: auto;
                vertical-align: top;
            }
            img.cma-screenshot {
                margin: 10px 0 0 25px;
                max-width: 800px;
                height: auto;
                vertical-align: top;
                border: 1px solid #000;
            }
            div.cma-screenshot-text {
                margin: 10px 0 0 25px;
            }
            .cma-break-word {
                word-wrap: break-word;
            }
            .cma-tag {
                border-radius: 5px;
                padding: 1px 10px;
                margin-right: 10px;
            }
            .cma-tag-info {
                background: #f0f0f0;
            }
            .cma-tag-warning {
                background: #ffdead;
